mapper.select:
select

from consult_doctor_info
where 1=1

  and gender =  #{gender,jdbcType=VARCHAR}


  and name =  #{name,jdbcType=VARCHAR}


  and sys_user_id =  #{userId,jdbcType=VARCHAR}


  and id =  #{id,jdbcType=VARCHAR}


  and type =  #{type,jdbcType=VARCHAR}


  and open_id =  #{openId,jdbcType=VARCHAR}



  and start_doctor = #{startDoctor,jdbcType=VARCHAR}


  and nonrealtime_status = #{nonrealtimeStatus,jdbcType=VARCHAR}

order by sort

SELECT

from consult_doctor_info where 1=1

  and grab_session = #{grabSession,jdbcType=VARCHAR}


  and send_message = #{sendMessage,jdbcType=VARCHAR}


  and receive_differential_notification = #{receiveDifferentialNotification,jdbcType=VARCHAR}


  and department = #{department,jdbcType=VARCHAR}


  and type = #{type,jdbcType=VARCHAR}


  and nonrealtime_status = #{nonrealtimeStatus,jdbcType=VARCHAR}
