mapper.select:
SELECT su.*, cdi.department, consultSession.currentCount currentCount FROM sys_user su LEFT JOIN consult_doctor_info cdi ON cdi.sys_user_id = su.id LEFT JOIN ( SELECT sys_user_id_cs id, count(*) CurrentCount FROM consult_session WHERE DATE_FORMAT(create_time, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d') AND sys_user_id_cs IN
#{classIdList}
AND state = 'ongoing' GROUP BY sys_user_id_cs ORDER BY NULL ) consultSession ON cdi.sys_user_id = consultSession.id WHERE su.id IN
#{classIdList}
