mapper.select:
SELECT * FROM insurance_register_service irs WHERE baby_id = #{babyId}
AND irs.Insurance_type=#{insuranceType}
AND end_time > NOW() AND state != 6

SELECT irs.id, irs.parent_type, irs.Insurance_type, irs.parent_name, irs.state, irs.parent_phone, irs.id_card, irs.create_time, irs.update_time, irs.start_time, irs.end_time, irs.baby_id, irs.update_by, irs.source, sa.nickName, sbb.name FROM insurance_register_service irs LEFT JOIN sys_user su ON irs.parent_phone = su.phone LEFT JOIN sys_attention sa ON sa.openid = su.openid LEFT JOIN sys_baby_baseInfo sbb ON sbb.id = irs.baby_id where 1=1
AND irs.state=#{state}
AND irs.parent_phone=#{parentPhone}
AND irs.create_time >= #{fromOrderDate}
AND #{toOrderDate} >= irs.create_time
AND irs.start_time >= #{fromStartDate}
AND #{toStartDate} >= irs.start_time
and sbb.name = #{babyName}
and irs.Insurance_type = #{insuranceType}
group by irs.id ORDER BY irs.create_time desc

   SELECT * FROM insurance_register_service irs 
	INNER JOIN sys_baby_baseInfo sbb ON irs.baby_id=sbb.id
	where sbb.openid=#{openid}

SELECT irs.*,sbb.`name` FROM insurance_register_service irs INNER JOIN sys_baby_baseInfo sbb ON irs.baby_id=sbb.id where sbb.userid=#{userid} and ( irs.state=1 or irs.state=2 or irs.state=0 )
AND irs.Insurance_type=#{insuranceType}
ORDER BY create_time desc

SELECT irs.*,sbb.`name` FROM insurance_register_service irs INNER JOIN sys_baby_baseInfo sbb ON irs.baby_id=sbb.id where sbb.userid=#{userid} and ( irs.state=3 or irs.state=4 or irs.state=5 )
AND irs.Insurance_type=#{insuranceType}
ORDER BY create_time desc

SELECT * FROM insurance_register_service irs INNER JOIN sys_baby_baseInfo sbb ON irs.baby_id = sbb.id WHERE 1=1
and sbb.userid = #{parentId}
and irs.baby_id = #{babyId}

SELECT irs.id, irs.parent_type, irs.Insurance_type, irs.parent_name, irs.state, irs.parent_phone, irs.id_card, irs.create_time, irs.update_time, irs.start_time, irs.end_time, irs.baby_id, irs.update_by, irs.source, sa.nickName, sbb.name FROM insurance_register_service irs LEFT JOIN sys_user su ON irs.parent_phone = su.phone LEFT JOIN sys_attention sa ON sa.openid = su.openid LEFT JOIN sys_baby_baseInfo sbb ON sbb.id = irs.baby_id where 1=1
AND irs.state=#{state}
AND irs.parent_phone=#{parentPhone}
AND irs.create_time >= #{fromOrderDate}
AND #{toOrderDate} >= irs.create_time
AND irs.start_time >= #{fromStartDate}
AND #{toStartDate} >= irs.start_time
and sbb.name = #{babyName}
and irs.Insurance_type = #{insuranceType}
group by irs.id ORDER BY irs.create_time desc
