mapper.select:
select count(*)
from patient_register_praise prp
where prp.sys_doctor_id = #{doctorId}


    and evaluateType = #{evaluateType,jdbcType=VARCHAR}

select INSERT(prp.phone,4,6,'****') phone, prp.impression, prp.praise_date as date, prp.star, prp.major_star as majorStar, prp.wechat_name, prp.pic_url from patient_register_praise prp where prp.sys_doctor_id = #{doctorId}
and evaluateType = '1'
and (evaluateType <> '1' or evaluateType is null)
ORDER BY prp.praise_date DESC
limit #{limit}

SELECT * FROM customerEvaluation WHERE 1=1
and doctorId =#{doctorId}
and consult_session_id =#{consultSessionId}
order by createtime asc

SELECT star,praise_date,phone,praise FROM patient_register_praise
where 1=1

    and sys_doctor_id = #{sys_doctor_id}
