mapper.select:
select 

,

from non_real_time_consult_session
where 1=1

  and #{id,jdbcType=INTEGER}


  and #{status,jdbcType=VARCHAR}


  and #{userId,jdbcType=VARCHAR}


  and #{userName,jdbcType=VARCHAR}


  and #{csUserId,jdbcType=VARCHAR}


  and #{csUserName,jdbcType=VARCHAR}


  and #{source,jdbcType=VARCHAR}


  and #{lastMessageTime,jdbcType=TIMESTAMP}


  and #{lastMessageType,jdbcType=VARCHAR}


  and #{consultNumber,jdbcType=INTEGER}


  and #{doctorDepartmentName,jdbcType=VARCHAR}


  and #{doctorProfessor,jdbcType=VARCHAR}


  and #{createTime,jdbcType=TIMESTAMP}


  and #{updateTime,jdbcType=TIMESTAMP}


  and #{bak1,jdbcType=VARCHAR}


  and #{bak2,jdbcType=VARCHAR}


  and #{bak3,jdbcType=VARCHAR}


  and #{lastMessageContent,jdbcType=LONGVARCHAR}


  order by lastMessageTime ASC


  order by lastMessageTime DESC
