mapper.select:
select *,DATE_FORMAT(create_date,'%Y-%m-%d') date from sys_statistics where 1=1
and DATE_FORMAT(create_date,'%Y-%m-%d') between #{fromDate} and #{toDate}
ORDER BY DATE_FORMAT(create_date,'%Y-%m-%d') DESC

SELECT attention.department,attention.attentionCount,attention.cancleAttentionCount,attention.leijiAttentionCount,attention.leijiCancleAttentionCount,IFNULL(chat.chatCount,0) chatCount ,IFNULL(chat.validChatCount,0) validChatCount,IFNULL(chat.invalidChatCount,0) invalidChatCount,IFNULL(chat.chatScale,'0.00%') chatScale from (SELECT attentionLeiji1.department,attentionLeiji1.leijiAttentionCount,attentionLeiji1.leijiCancleAttentionCount,attentionLeiji2.attentionCount,attentionLeiji2.cancleAttentionCount from (SELECT chi.department, count(case sa.status when '0' then 1 end) as leijiAttentionCount, count(case sa.status when '1' then 1 end) as leijiCancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where sa.date <= #{endDate} GROUP BY chi.department) attentionLeiji1 LEFT JOIN (SELECT chi.department, count(case sa.status when '0' then 1 end) as attentionCount, count(case sa.status when '1' then 1 end) as cancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where sa.date >= #{startDate} and sa.date <= #{endDate} GROUP BY chi.department) attentionLeiji2 on attentionLeiji1.department=attentionLeiji2.department ) attention LEFT JOIN (SELECT chi.department,IFNULL(cs.flag,0),COUNT(*) as chatCount, count(case cs.flag when 0 then 1 end) as validChatCount, count(case when cs.flag!=0 then 1 end) as invalidChatCount, concat ( left (count(case cs.flag when 0 then 1 end)/COUNT(*)*100,5),'%') as chatScale from consult_session cs INNER JOIN sys_attention sa on cs.sys_user_id=sa.openid INNER JOIN channel_info chi on sa.marketer=chi.marketer where cs.create_time >= #{startDate} and cs.create_time <= #{endDate} GROUP BY chi.department) chat on attention.department=chat.department
where attention.department = #{department}
ORDER BY attention.department

SELECT attention.marketer,attention.attentionCount,attention.cancleAttentionCount,attention.leijiAttentionCount,attention.leijiCancleAttentionCount,IFNULL(chat.chatCount,0) chatCount ,IFNULL(chat.validChatCount,0) validChatCount,IFNULL(chat.invalidChatCount,0) invalidChatCount,IFNULL(chat.chatScale,'0.00%') chatScale,attention.channel,attention.department from (SELECT attentionLeiji1.marketer,attentionLeiji1.leijiAttentionCount,attentionLeiji1.leijiCancleAttentionCount,attentionLeiji2.attentionCount,attentionLeiji2.cancleAttentionCount ,attentionLeiji1.channel,attentionLeiji1.department from (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as leijiAttentionCount, count(case sa.status when '1' then 1 end) as leijiCancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where sa.date <= #{endDate} GROUP BY chi.marketer) attentionLeiji1 LEFT JOIN (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as attentionCount, count(case sa.status when '1' then 1 end) as cancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where sa.date >= #{startDate} and sa.date <= #{endDate} GROUP BY chi.marketer) attentionLeiji2 on attentionLeiji1.marketer=attentionLeiji2.marketer ) attention LEFT JOIN (SELECT chi.marketer,chi.channel,chi.department,IFNULL(cs.flag,0),COUNT(*) as chatCount, count(case cs.flag when 0 then 1 end) as validChatCount, count(case when cs.flag!=0 then 1 end) as invalidChatCount, concat ( left (count(case cs.flag when 0 then 1 end)/COUNT(*)*100,5),'%') as chatScale from consult_session cs INNER JOIN sys_attention sa on cs.sys_user_id=sa.openid INNER JOIN channel_info chi on sa.marketer=chi.marketer where cs.create_time >= #{startDate} and cs.create_time <= #{endDate} GROUP BY chi.marketer) chat on attention.marketer=chat.marketer
where attention.department = #{department} and attention.channel = #{channel}
where attention.department = #{department}
where attention.channel = #{channel}
ORDER BY attention.marketer

SELECT attention.marketer,attention.attentionCount,attention.cancleAttentionCount,attention.leijiAttentionCount,attention.leijiCancleAttentionCount,attention.channel,attention.department from (SELECT attentionLeiji1.marketer,attentionLeiji1.leijiAttentionCount,attentionLeiji1.leijiCancleAttentionCount,attentionLeiji2.attentionCount,attentionLeiji2.cancleAttentionCount ,attentionLeiji1.channel,attentionLeiji1.department from (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as leijiAttentionCount, count(case sa.status when '1' then 1 end) as leijiCancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where 1=1
and sa.date >= #{startDate}
and sa.date <= #{endDate}
GROUP BY chi.marketer) attentionLeiji1 LEFT JOIN (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as attentionCount, count(case sa.status when '1' then 1 end) as cancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where 1=1
and sa.date >= #{startDate}
and sa.date <= #{endDate}
GROUP BY chi.marketer) attentionLeiji2 on attentionLeiji1.marketer=attentionLeiji2.marketer ) attention where 1=1
and attention.department = #{department}
and attention.channel = #{channel}
ORDER BY attention.marketer

SELECT attention.marketer,attention.attentionCount,attention.cancleAttentionCount,attention.leijiAttentionCount,attention.leijiCancleAttentionCount,attention.channel,attention.department from (SELECT attentionLeiji1.marketer,attentionLeiji1.leijiAttentionCount,attentionLeiji1.leijiCancleAttentionCount,attentionLeiji2.attentionCount,attentionLeiji2.cancleAttentionCount ,attentionLeiji1.channel,attentionLeiji1.department from (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as leijiAttentionCount, count(case sa.status when '1' then 1 end) as leijiCancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where 1=1
and sa.date >= #{startDate}
and sa.date <= #{endDate}
GROUP BY chi.department) attentionLeiji1 LEFT JOIN (SELECT chi.marketer, chi.channel, chi.department, count(case sa.status when '0' then 1 end) as attentionCount, count(case sa.status when '1' then 1 end) as cancleAttentionCount from channel_info chi INNER JOIN sys_attention sa ON sa.marketer = chi.marketer where 1=1
and sa.date >= #{startDate}
and sa.date <= #{endDate}
GROUP BY chi.department) attentionLeiji2 on attentionLeiji1.marketer=attentionLeiji2.marketer ) attention where 1=1
and attention.department = #{department}
and attention.channel = #{channel}
ORDER BY attention.marketer

SELECT chi.marketer,chi.channel,chi.department,COUNT(DISTINCT openid) totalConsultCounts from consult_session cs
INNER JOIN sys_attention sa
ON cs.sys_user_id=sa.openid
INNER JOIN channel_info chi
on sa.marketer=chi.marketer
where chi.marketer IS NOT NULL and chi.marketer !=''
and cs.flag IS NULL
and DATE_FORMAT(cs.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}


        and chi.department = #{department} and chi.channel = #{channel}


        and chi.department = #{department}




        and  chi.channel = #{channel}


GROUP BY chi.marketer
ORDER BY chi.marketer

SELECT chi.marketer,chi.channel,chi.department,COUNT(DISTINCT openid) newConsultCounts from consult_session cs
INNER JOIN sys_attention sa
ON cs.sys_user_id=sa.openid
INNER JOIN channel_info chi
on sa.marketer=chi.marketer
where chi.marketer IS NOT NULL and chi.marketer !=''
and sa.status='0'
and cs.flag IS NULL
and DATE_FORMAT(cs.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
and DATE_FORMAT(sa.date,'%Y-%m-%d') BETWEEN #{startDate} and #{endDate}


        and chi.department = #{department} and chi.channel = #{channel}


        and chi.department = #{department}




        and  chi.channel = #{channel}


GROUP BY chi.marketer
ORDER BY chi.marketer

SELECT chi.department,COUNT(DISTINCT openid) totalConsultCounts from consult_session cs INNER JOIN sys_attention sa ON cs.sys_user_id=sa.openid INNER JOIN channel_info chi on sa.marketer=chi.marketer where chi.marketer IS NOT NULL and chi.marketer !='' and cs.flag IS NULL and DATE_FORMAT(cs.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
and chi.department = #{department}
GROUP BY chi.department ORDER BY chi.department

SELECT chi.department,COUNT(DISTINCT openid) newConsultCounts from consult_session cs INNER JOIN sys_attention sa ON cs.sys_user_id=sa.openid INNER JOIN channel_info chi on sa.marketer=chi.marketer where chi.marketer IS NOT NULL and chi.marketer !='' and sa.status='0' and cs.flag IS NULL and DATE_FORMAT(cs.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate} and DATE_FORMAT(sa.date,'%Y-%m-%d') BETWEEN #{startDate} and #{endDate}
and chi.department = #{department}
GROUP BY chi.department ORDER BY chi.department
