mapper.select:
SELECT crs.id, sd.id doctorId, sd. NAME doctorName, cdr.doctor_answer_phone doctorPhone, DATE_FORMAT(scs.beginTime, '%k:%i') beginTime, DATE_FORMAT(scs.endTime, '%k:%i') endTime, DATE_FORMAT(scs.date, '%Y/%m/%d') date, DATE_FORMAT( crs.create_time, '%Y/%m/%d %k:%i' ) orderTime, crs.register_no AS orderNo, crs.phone userPhone, sbi. NAME babyName, cdr.price, crs.type, crs.surplusTime, su.openid, cdr.server_length conversationLength FROM consultPhone_register_service crs LEFT JOIN sys_consultPhone_service scs ON crs.sys_phoneConsult_service_id = scs.id LEFT JOIN consultphone_doctor_relation cdr ON cdr.doctor_id = scs.sys_doctor_id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN sys_patient sp ON sp.id = crs.sys_patient_id LEFT JOIN sys_baby_baseInfo sbi ON sp.sys_user_id = sbi.userid LEFT JOIN sys_user su on sp.sys_user_id = su.id where 1=1
and crs.state = #{state,jdbcType=VARCHAR}
AND ( #{date,jdbcType=TIMESTAMP}>=DATE_FORMAT( CONCAT( DATE_FORMAT(scs.date, '%Y:%c:%d'), DATE_FORMAT(scs.beginTime, ' %k:%i:%s') ), '%Y-%m-%d %k:%i:%s' ) )

SELECT sd.id as doctorId, sd.name, sd.position2, sd.hospitalName, crs.id, case when crs.state ='0' then '待支付' when crs.state ='1' then '待接听' when crs.state ='2' then '待评价' when crs.state ='3' then '待分享' when crs.state ='4' then '已取消' when crs.state ='5' then '已完成' end state, crs.create_time, crs.register_no, crs.illness_describe_id, scp.date, scp.beginTime, scp.endTime, 'phone' as classify FROM consultPhone_register_service crs LEFT JOIN sys_patient sp ON crs.sys_patient_id = sp.id LEFT JOIN sys_consultPhone_service scp ON scp.id = crs.sys_phoneConsult_service_id LEFT JOIN sys_doctor sd ON sd.id = scp.sys_doctor_id WHERE sp.sys_user_id = #{userId,jdbcType=VARCHAR}
and crs.state = #{state,jdbcType=VARCHAR}
order by scp.date,scp.beginTime desc

SELECT sd.id as doctorId, sd.name, sd.position2, sd.hospitalName, crs.id, case when crs.state ='0' then '待支付' when crs.state ='1' then '待接听' when crs.state ='2' then '待评价' when crs.state ='3' then '待分享' when crs.state ='4' then '已取消' when crs.state ='5' then '已完成' end state, crs.create_time, crs.register_no, crs.illness_describe_id, scp.date, scp.beginTime, scp.endTime, 'phone' as classify FROM consultPhone_register_service crs LEFT JOIN sys_patient sp ON crs.sys_patient_id = sp.id LEFT JOIN sys_consultPhone_service scp ON scp.id = crs.sys_phoneConsult_service_id LEFT JOIN sys_doctor sd ON sd.id = scp.sys_doctor_id WHERE sp.sys_user_id = #{userId,jdbcType=VARCHAR}
and crs.state = #{state,VARCHAR}
UNION SELECT sd.id as doctorId, sd.name, sd.position2, sd.hospitalName, prs.id, case when prs.status ='0' then '待支付' when prs.status ='1' then '待就诊' when prs.status ='2' then '待评价' when prs.status ='3' then '待分享' when prs.status ='4' then '已取消' when prs.status ='5' then '已完成' end state, prs.create_date as create_time, prs.register_no, prs.illness as illness_describe_id, srp.date, srp.begin_time as beginTime, srp.end_time as endTime, 'ap' as classify FROM patient_register_service prs LEFT JOIN sys_patient sp ON prs.sys_patient_id = sp.id LEFT JOIN sys_register_service srp ON srp.id = prs.sys_register_service_id LEFT JOIN sys_doctor sd ON sd.id = srp.sys_doctor_id WHERE sp.sys_user_id = #{userId,jdbcType=VARCHAR}
and prs.status = #{state,VARCHAR}
order by prs.create_time desc

SELECT srs.beginTime, srs.endTime, sd.position2 position, sbb.name, hid.illness, sd.subsidy, cdr.price FROM sys_consultphone_service srs RIGHT JOIN consultphone_register_service prs ON srs.id = prs.sys_phoneConsult_service_id AND prs. state != "4" AND prs. state != "1" AND prs. state != "0" LEFT JOIN healthrecord_illness_describe hid on hid.id = prs.illness_describe_id LEFT JOIN sys_baby_baseinfo sbb ON sbb.id = hid.babyinfo_Id INNER JOIN sys_doctor sd ON sd.id = srs.sys_doctor_id
AND srs.date=#{date}
AND srs.sys_doctor_id = #{doctorId}
LEFT JOIN consultphone_doctor_relation cdr ON cdr.doctor_id = sd.id ORDER BY srs.beginTime ASC

mapper.update:
 update consultphone_register_service prs

    prs.state=#{state}

where prs.id=#{id};

mapper.select:
SELECT crs.id, crs.register_no, crs.create_time, crs.phone AS phoneNum, crs.update_time, crs.delete_by, crs.state, crs.surplusTime, scs.date, scs.beginTime, scs.endTime, cdr.server_length AS type, cdr.price, cdr.doctor_answer_phone, sd. NAME AS doctorName, sd.id AS doctorId, su.phone AS loginPhone, sa.nickname, sbb. NAME AS babyName FROM consultphone_register_service crs LEFT JOIN sys_consultphone_service scs ON scs.id = crs.sys_phoneConsult_service_id LEFT JOIN consultphone_doctor_relation cdr ON cdr.doctor_id = scs.sys_doctor_id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN sys_patient sp ON sp.id = crs.sys_patient_id LEFT JOIN sys_user su ON su.id = sp.sys_user_id LEFT JOIN sys_attention sa ON sa.openid = su.openid LEFT JOIN healthrecord_illness_describe hid ON hid.id = crs.illness_describe_id LEFT JOIN sys_baby_baseinfo sbb ON sbb.id = hid.babyinfo_Id WHERE 1=1
and crs.state = #{state}
and crs.state in (1,2,3)
and crs.state = #{payState}
and su.phone = #{loginPhone}
and sd.NAME = #{doctorName}
and crs.create_time >= #{orderTimeFromStr}
and #{orderTimeToStr} >= crs.create_time
and scs.date >= #{consultPhoneTimeFromStr}
and #{consultPhoneTimeToStr} >= scs.date

SELECT crs.id, crs.register_no, crs.create_time, crs.phone AS phoneNum, crs.update_time, crs.delete_by, crs.state, crs.surplusTime, scs.date, scs.beginTime, scs.endTime, cdr.server_length AS type, cdr.price, cdr.doctor_answer_phone, sd. NAME AS doctorName, sd.id AS doctorId, su.phone AS loginPhone, sa.nickname, sbb. NAME AS babyName FROM consultphone_register_service crs LEFT JOIN sys_consultphone_service scs ON scs.id = crs.sys_phoneConsult_service_id LEFT JOIN consultphone_doctor_relation cdr ON cdr.doctor_id = scs.sys_doctor_id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN sys_patient sp ON sp.id = crs.sys_patient_id LEFT JOIN sys_user su ON su.id = sp.sys_user_id LEFT JOIN sys_attention sa ON sa.openid = su.openid LEFT JOIN healthrecord_illness_describe hid ON hid.id = crs.illness_describe_id LEFT JOIN sys_baby_baseinfo sbb ON sbb.id = hid.babyinfo_Id where 1=1
and crs.state = #{state}
and crs.id = #{id}
and crs.sys_phoneConsult_service_id = #{registerId}
and sd.id = #{doctorId}
and scs.date > date_sub(now(),interval 1 week)

SELECT crs.id, crs.register_no AS registerNo, crs.create_time AS createTime, crs.phone AS loginPhone, crs.update_time AS updateTime, crs.delete_by AS deleteBy, crs.state, crs.surplusTime, scs.date, scs.beginTime, scs.endTime, cdr.server_length AS type, cdr.price, cdr.doctor_answer_phone AS phoneNum, sd. NAME AS doctorName, sd.id AS doctorId, sa.nickname AS nickName, sbb. NAME AS babyName FROM consultphone_register_service crs LEFT JOIN sys_consultphone_service scs ON scs.id = crs.sys_phoneConsult_service_id LEFT JOIN consultphone_doctor_relation cdr ON cdr.doctor_id = scs.sys_doctor_id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN sys_patient sp ON sp.id = crs.sys_patient_id LEFT JOIN sys_user su ON su.id = sp.sys_user_id LEFT JOIN sys_attention sa ON sa.openid = su.openid LEFT JOIN healthrecord_illness_describe hid ON hid.id = crs.illness_describe_id LEFT JOIN sys_baby_baseinfo sbb ON sbb.id = hid.babyinfo_Id where 1=1
and crs.state = #{state}
and crs.state in (1,2,3)
and crs.state = #{payState}
and crs.id = #{id}
and sd.id = #{doctorId}
