update sys_register_service srs

   srs.status='1'

        ,srs.sys_patient_id=#{sys_patient_id}


        ,srs.appointmentNo=#{register_no}


where srs.id=#{sys_register_service_id};

update sys_register_service srs

    srs.status='0'

where srs.appointmentNo=#{register_no};

mapper.select:
        SELECT srs.*         
        FROM sys_register_service srs
        inner join sys_doctor_location sdl on sdl.id=srs.location_id
        WHERE 1=1

           and srs.sys_doctor_id=#{sysDoctorId}


           and srs.sys_hospital_id=#{sysHospitalId}


           and srs.date=#{date}


           and sdl.location=#{location}


           and srs.status=#{status}


           and srs.location_id=#{locationId}


	     	 and srs.date in 

	 			#{item}



	     	 and srs.begin_time=#{time}


	     	 and srs.begin_time in 

	 			#{item}



	     	 and srs.status in 

	 			#{item}



	     	 and srs.repeatFlag in 

	 			#{item}



           and srs.begin_time >= #{fromTime}


           and srs.begin_time < #{toTime}

     	order by date desc

SELECT srs.id, srs.price, srs.date, srs.begin_time, srs.sys_hospital_id, sdl.hospitalName, su.name doctorName, su.phone, srs.status, sdl.location, dhr.department_level1 AS department, dhr.relation_type AS relationType FROM sys_register_service srs LEFT JOIN sys_doctor sd ON srs.sys_doctor_id = sd.id left JOIN doctor_hospital_relation dhr ON dhr.sys_doctor_id = srs.sys_doctor_id LEFT JOIN sys_user su ON su.id = sd.sys_user_id LEFT JOIN sys_doctor_location sdl ON sdl.id = srs.location_id where (DATE_FORMAT(CONCAT(DATE_FORMAT(srs.date,'%Y:%c:%d'),DATE_FORMAT(srs.begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))>now() and date_sub(now(),interval -1 month) >= (DATE_FORMAT(CONCAT(DATE_FORMAT(srs.date,'%Y:%c:%d'),DATE_FORMAT(srs.begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))
AND srs.sys_doctor_id=#{sysDoctorId}
AND srs.sys_hospital_id=#{sysHospitalId}
AND srs.status=#{status}
AND su.name like CONCAT('%',#{doctorName},'%')
AND #{priceRange} > srs.price
AND srs.price >= 100 AND 300 >= srs.price
AND srs.price > #{priceRange}
AND sd.hospitalName=#{location}
AND srs.date >= #{fromDate}
AND #{toDate} >= srs.date
AND srs.sys_doctor_id in (SELECT distinct sys_doctor_id FROM doctor_illness_relation where sys_illness_id in (SELECT id FROM sys_illness where level_1=#{department}))
AND dhr.relation_type=#{relationType}
group by srs.id
ORDER BY ${page.orderBy}
order by (DATE_FORMAT(CONCAT(DATE_FORMAT(srs.date,'%Y:%c:%d'),DATE_FORMAT(srs.begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s')) asc

    	select  srs.price,srs.date,srs.begin_time,sdl.location,su.name doctorName,su.phone from sys_register_service srs
    	left join sys_doctor sd on srs.sys_doctor_id=sd.id  
    	left join sys_user su on su.id=sd.sys_user_id 
        inner join sys_doctor_location sdl on sdl.id=srs.location_id
        where srs.status='0'

			AND srs.sys_doctor_id=#{sysDoctorId}


			AND srs.id=#{sysRegisterId}


			AND su.name=#{doctorName}


			AND sd.hospitalName=#{location}

select srs.date,sdl.kindly_reminder as kindlyReminder from sys_register_service srs inner join sys_hospital sh on srs.sys_hospital_id = sh.id inner join sys_doctor_location sdl on sdl.id=srs.location_id WHERE sh.name = #{hospitalName} AND srs.sys_doctor_id = #{doctorId} and srs.status='0' AND srs.location_id = #{location_id}
and srs.status=#{status}
AND (DATE_FORMAT(CONCAT(DATE_FORMAT(srs.DATE,'%Y:%c:%d'),DATE_FORMAT(srs.begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))>NOW() GROUP BY srs.date ORDER BY (DATE_FORMAT(CONCAT(DATE_FORMAT(srs.DATE,'%Y:%c:%d'),DATE_FORMAT(srs.begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s')) ASC

SELECT DATE_FORMAT(srs.date, '%Y/%m/%d') AS DATE, srs.id, srs.sys_doctor_id, srs.sys_patient_id, srs.sys_hospital_id, srs.location_id, srs.repeatFlag, sdl.hospitalName, srs.price, DATE_FORMAT(srs.date, '%m-%d') AS DATE, DATE_FORMAT(srs.begin_time, '%H:%i') AS begin_time, DATE_FORMAT(srs.end_time, '%H:%i') AS end_time, sdl.location, sdl.route AS root, srs.status, srs.service_type FROM sys_register_service srs INNER JOIN sys_hospital shs on shs.id = srs.sys_hospital_id INNER JOIN sys_doctor_location sdl ON sdl.id = srs.location_id WHERE 1=1
and shs.hospital_type!='2'
and srs.sys_doctor_id = #{doctorId}
AND date = #{date}
and (status = "0" or status = "1")
AND srs.location_id = #{location_id}
ORDER BY srs.begin_time asc

SELECT distinct(srs.date) date from sys_register_service srs, sys_hospital shs WHERE shs.hospital_type!='2' and srs.sys_doctor_id = #{doctorId} and shs.id = srs.sys_hospital_id and (srs.status = "0" or srs.status = "1")
AND srs.date >= #{from}
AND srs.date < #{to}
ORDER BY srs.date asc

SELECT MIN(DATE) as availableDate FROM sys_register_service
WHERE STATUS=0  AND (DATE_FORMAT(CONCAT(DATE_FORMAT(DATE,'%Y:%c:%d'),DATE_FORMAT(begin_time,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))>NOW()
AND sys_doctor_id=#{doctorId}
