mapper.select:
select * from sys_consultPhone_service scs
where scs.sys_doctor_id = #{doctorId,jdbcType=VARCHAR}
and scs.state = #{state,jdbcType=VARCHAR}
and (DATE_FORMAT(CONCAT(DATE_FORMAT(scs.date,'%Y:%c:%d'),DATE_FORMAT(scs.beginTime,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))>now()
and date_sub(now(),interval -1 month) >= (DATE_FORMAT(CONCAT(DATE_FORMAT(scs.date,'%Y:%c:%d'),DATE_FORMAT(scs.beginTime,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))
order by scs.createDate desc

select DISTINCT(DATE_FORMAT(scs.date, '%Y-%m-%d')) as date from sys_consultphone_service scs WHERE scs.sys_doctor_id = #{doctorId}
and scs.state=#{state}
AND (DATE_FORMAT(CONCAT(DATE_FORMAT(scs.DATE,'%Y:%c:%d'),DATE_FORMAT(scs.beginTime,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s'))>NOW() GROUP BY scs.date ORDER BY (DATE_FORMAT(CONCAT(DATE_FORMAT(scs.DATE,'%Y:%c:%d'),DATE_FORMAT(scs.beginTime,' %k:%i:%s')),'%Y-%m-%d %k:%i:%s')) ASC

select scs.id,scs.price, DATE_FORMAT(scs.beginTime, '%H:%i') AS begin_time, DATE_FORMAT(scs.endTime, '%H:%i') AS end_time, scs.state, scs.serviceType FROM sys_consultphone_service scs WHERE 1=1 and scs.sys_doctor_id = #{doctorId}
AND date = #{date}
ORDER BY scs.beginTime asc

mapper.update:
update sys_consultPhone_service
set state = #{state,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
