mapper.select:
SELECT *,COUNT(pr.babyName) from ( SELECT pr.babyName, pr.birthday, pr.phone, pr.illness, sd.hospitalName, sd.`name` as dname, pr.create_date, apr.pay_date, op.charge as `status` FROM patient_register_service pr LEFT JOIN sys_register_service srs ON pr.sys_register_service_id = srs.id LEFT JOIN sys_doctor sd ON srs.sys_doctor_id=sd.id LEFT JOIN sys_patient sp ON pr.sys_patient_id = sp.id LEFT JOIN sys_user su ON su.id = sp.sys_user_id LEFT JOIN account_pay_record apr ON apr.order_id=pr.id LEFT JOIN order_property op ON op.patient_register_service_id=pr.id WHERE su.openid =#{openid}
AND pr.babyName =#{babyName}
AND pr.`status`!=6 ORDER BY pr.create_date desc ) pr GROUP BY pr.babyName order by pr.create_date desc

SELECT MAX(pr.create_date) FROM patient_register_service pr
INNER JOIN sys_patient sp ON pr.sys_patient_id = sp.id
INNER JOIN sys_user su ON su.id = sp.sys_user_id
WHERE
	su.openid=#{openid}
and pr.babyName=#{babyname}
