mapper.update:
update sys_monitor SET types='0'
where id IN

    #{classList}

mapper.select:
select DISTINCT(sd.name) AS doctorName, case when shc.sys_hospital_name is null then "" else shc.sys_hospital_name end hospitalName, su.openid openid, cdr.doctor_answer_phone phone FROM sys_consultphone_service scs INNER JOIN consultPhone_register_service crs ON crs.sys_phoneConsult_service_id = scs.id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN consultPhone_doctor_relation cdr ON cdr.doctor_id = sd.id LEFT JOIN doctor_hospital_relation dhr on sd.id = dhr.sys_doctor_id LEFT JOIN sys_hospital_contact shc on shc.sys_hospital_id = dhr.sys_hospital_id LEFT JOIN sys_user su ON su.id = sd.sys_user_id where crs. state = '1' AND DATE_FORMAT(scs.date,"%Y-%m-%d") = #{date}
AND DATE_FORMAT(crs.create_time,"%Y-%m-%d %H:%i:%s") > #{startTime}
AND DATE_FORMAT(crs.create_time,"%Y-%m-%d %H:%i:%s") < #{endTime}
ORDER BY scs.beginTime

select sbb.name as babyName, crs.id AS patient_register_service_id, case when shc.sys_hospital_name is null then "" else shc.sys_hospital_name end hospitalName, DATE_FORMAT(scs.beginTime,"%H:%i") begin_time, DATE_FORMAT(scs.endTime,"%H:%i") end_time, sd.name AS doctorName, cdr.doctor_answer_phone as phone, case when su.openid is null then "" else su.openid end FROM sys_consultphone_service scs INNER JOIN consultPhone_register_service crs ON crs.sys_phoneConsult_service_id = scs.id LEFT JOIN healthrecord_illness_describe hid ON hid.id = crs.illness_describe_id LEFT JOIN sys_baby_baseInfo sbb ON sbb.id = hid.babyinfo_Id LEFT JOIN sys_doctor sd ON sd.id = scs.sys_doctor_id LEFT JOIN consultPhone_doctor_relation cdr ON cdr.doctor_id = sd.id LEFT JOIN doctor_hospital_relation dhr on sd.id = dhr.sys_doctor_id LEFT JOIN sys_hospital sh on dhr.sys_hospital_id = sh.id LEFT JOIN sys_hospital_contact shc on shc.sys_hospital_id = dhr.sys_hospital_id LEFT JOIN sys_user su ON su.id = sd.sys_user_id where crs. state = '1' AND DATE_FORMAT(scs.date,"%Y-%m-%d") = #{date}
AND DATE_FORMAT(crs.create_time,"%Y-%m-%d %H:%i:%s") > #{startTime}
AND DATE_FORMAT(crs.create_time,"%Y-%m-%d %H:%i:%s") < #{endTime}
ORDER BY scs.beginTime

mapper.update:
UPDATE sys_wechatparameter sw
sw.token=#{token}, sw.ticket=#{ticket}, sw.updateTime = now()
where sw.id=#{id};
