mapper.select:
select * from baby_umbrellainfo
where 1=1

    and create_time like '${today}%' and pay_result!='fail'


    and openid = #{openid}


    and baby_id is NULL


    and ifshare is NULL


    and id = #{id}


    and DATEDIFF(NOW(),create_time) = #{notShareOrActiveDays}

select IFNULL(sum(umbrella_money),0) from baby_umbrellainfo
where 1=1

    and openid = #{openid}

SELECT id,parent_phone,parent_name,pay_result,create_time createTime from baby_umbrellainfo
where pay_result='success'

    and parent_phone=#{userPhone}


    and openid=#{openId}

select ( select count(*) from `baby_umbrellainfo` bl where 1=1
and bl.`create_time` < #{date}
and (bl.`pay_result`='success' OR bl.`pay_result` is null) and `baby_id` is not null and `baby_id` !='' and bl.openid !='' )
+(select count(*)*2 from `baby_umbrellainfo` bl where 1=1
and bl.`create_time` < #{date}
and (bl.`pay_result`='success' OR bl.`pay_result` is null) and `baby_id` is null and bl.openid !='' )
+(select count(*) from `umbrella_familyinfo` where `umbrella_id` in (select id from `baby_umbrellainfo` bl where 1=1
and bl.`create_time` < #{date}
and (bl.`pay_result`='success' OR bl.`pay_result` is null) and bl.openid !='' ) )
as a

select count(*) from `baby_umbrellainfo` bl where 1=1
and bl.`create_time` < #{date}
and (bl.`pay_result`='success' OR bl.`pay_result` is null) and bl.openid !=''
