mapper.select:
select 

,

from vaccine_send_message
where 1=1

  and id = #{id,jdbcType=INTEGER}


  and create_time  like '${searchCreateTime}%'


  and  #{sendTime} > send_time


  and send_time  like '${searchTime}%'


  and sys_user_id = #{sysUserId}


  and vaccine_id  = #{vaccineId}


  and msg_type  = #{msgType}


  and valid_flag  = #{validFlag}

SELECT * from vaccine_send_message
WHERE
    send_time LIKE #{sendTime}
AND valid_flag =  #{validFlag}
GROUP BY sys_user_id
